<?php/**	File for testing the BaseElements Plug-In HTTP functions		echo both the full HTTP headers for the request and any POST arguments back to the user-agent		@project	BaseElements Plug-In	@link		https://github.com/nickorr/BaseElements-Plugin	@version	1.0.1	@since		November 16, 2011	@author		Mark Banks, https://github.com/minstral*//**	print out the key value pairs from the supplied has as HTML		@param hash $hash a hash (sic)	@return NULL*/	function print_hash ( $hash ) {				echo '<pre>';		foreach ( $hash as $key => $value ) {			echo $key . ": " . $value . '<br>';		}		echo '</pre>';	}?><!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01//EN" "http://www.w3.org/TR/html4/strict.dtd"><html lang="en"><head>	<meta http-equiv="Content-Type" content="text/html; charset=utf-8">	<title>BaseElements Plug-In HTTP Test Helper</title></head><body><?php	// POST Args	print_hash ( $_POST );	?><hr /><?php	// HTTP Headers	print_hash ( getallheaders() );	?></body></html>