<?php/**	File for testing the BaseElements Plug-In HTTP functions	echo both the full HTTP headers for the request and any POST arguments, username & password and the HTTP method back to the user-agent	@project	BaseElements Plug-In	@link		https://github.com/nickorr/BaseElements-Plugin	@version	1.0.4	@since		April 4, 2013	@author		Mark Banks, https://github.com/minstral*//**	print out the key value pairs from the supplied has as HTML	@param hash $hash a hash (sic)	@return NULL*/	function print_hash ( $hash ) {		foreach ( $hash as $key => $value ) {			echo $key . ": " . $value . '<br>';		}	}/**	print out line of text with a label, to be used between PRE tags	@param string $label a descriptive label or title	@param string $line the text to display	@return NULL*/	function print_line ( $label, $line ) {		echo $label . ": " . $line . "\n";	}?><!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01//EN" "http://www.w3.org/TR/html4/strict.dtd"><html lang="en"><head>	<meta http-equiv="Content-Type" content="text/html; charset=utf-8">	<title>BaseElements Plug-In HTTP Test Helper</title></head><body><?php	// make sure we ask for the username and password (if available)	header ( 'WWW-Authenticate: Basic realm="BaseElements Plug-In"' );	header ( 'HTTP/1.0 401 Unauthorized' );	$method = $_SERVER [ 'REQUEST_METHOD' ];	echo '<pre>';	print_line ( 'Method', $method );	print_line ( 'Path', $_SERVER [ 'REQUEST_URI' ] );	print_line ( 'Username', $_SERVER [ 'PHP_AUTH_USER' ] );	print_line ( 'Password', $_SERVER [ 'PHP_AUTH_PW' ] );	print_line ( 'Referrer', $_SERVER [ 'HTTP_REFERER' ] );	// POST Args	print_hash ( $_POST );	// PUT Data	echo file_get_contents ( 'php://input' );	echo '</pre>';?><hr /><?php	// HTTP Headers	print_hash ( getallheaders() );?></body></html>